mapper.select:
select count(1)
from
chuang_user_ticket
   where 1=1

       and order_sn=#{order_sn}


       and code=#{code}

select id,code,uid,is_used,order_id,order_sn,used_time,code_price
from
chuang_user_ticket
where 1=1

    and order_sn=#{order_sn}


        and code=#{code}

order by order_sn desc
limit #{startIndex},#{pageSize}

select count(1)
from (
        select ta.* from
        chuang_user_ticket_card_address ta
        ,chuang_shop_order o
        where ta.order_id =o.id
        and o.status ='1'

        and ta.status =#{status}


    and ta.mobile =#{mobile}


    and ta.order_sn =#{order_sn}

        )
        a

select ta.* from
chuang_user_ticket_card_address ta
,chuang_shop_order o
where ta.order_id =o.id
and o.status ='1'

    and ta.status =#{status}


    and ta.mobile =#{mobile}


    and ta.order_sn =#{order_sn}

limit #{startIndex},#{pageSize}
